insert into cities(id, name, info)
values (1, 'Москва', 'Не забудьте посетить Красную Площадь. Ну а в ЦУМ можно и не заходить)))'),
       (2, 'Шанхай', 'Шанхай является коммерческим центром Китая, одни из лучших отелей в стране, большое количество ресторанов и магазинов. Недалеко от Шанхая два замечательных места, городской округ Сучжоув и город Ханчжоу. При посещении Шанхая обязательно отправьтесь туда на однодневную экскурсию. Добраться до них можно на городских скоростных поездах. Сучжоу славится своими неповторимыми китайскими садами, а Ханчжоу знаменитым двухтысячелетним “Западным озером” и древними чайными плантациями. Все это служило источником вдохновения для большей части литературы страны, искусства и религии.'),
       (3, 'Паттайя', 'Суан Нонг Нуч, в Паттайе, возможно, самый красивый ботанический сад в регионе, сказочный мир декоративных водорослей и тропических растений, красивых домов и плодовых насаждений. Также парк проводит тайское культурное шоу. Он привлекает более чем 2000 посетителей в день, и содержит самую большую в мире коллекцию пальм и орхидей.'),
       (4, 'Майями', 'Созданный из болота на рубеже прошлого века, Майами является молодым и интересным городом, с глубоко укоренившейся латиноамериканской культурой. Для отдыха, лучше всего подойдет “Южный пляж” – модная площадка ослепительных отреставрированных отелей в стиле арт-деко, стильные рестораны, море ночных клубов и веселья.'),
       (5, 'Пхукет', 'Пхукет – жемчужина Андаманского моря. Самый крупный остров в Таиланде, извилистые береговые линии, покрытые широкими сахаристыми пляжами. Самый популярный курорт в Таиланде, окруженный китайско-португальской архитектурой.'),
       (6, 'Гуанчжоу', 'Город в Китае, 2-ой по росту популяции населения в мире за последний год. Богат культурными и историческими достопримечательностями, кроме того, множество древних храмов, ботанических садов и обширных тематических парков.'),
       (7, 'Тайбэй', 'Одно из немногих мест, где сохранилась культура и традиции старинного Китая. Материковая часть Китая подверглась гораздо большему воздействию западной культуры. Тайбэй сегодня является промышленным и экономическим центром Тайваня. Чтобы постигнуть великую древнюю культуру Китая, следует непременно отправиться сюда.'),
       (8, 'Рим', 'Руины и христианские места, ранней эпохи, поклонениями стоят здесь. Ренессансные палаццо, фонтаны в стиле барокко, мягкий средиземноморский климат, море, модные бутики и кухня – все это делает Рим одним из самых привлекательных туристических городов в мире.'),
       (9, 'Сеул', 'Некогда разоренный и угнетенный город, словно феникс, воспрял из пепла. Сегодня это передовая столица Южной Кореи, с большой перспективой по развитию во всех сферах деятельности.'),
       (10, 'Анталия', 'Бирюзовые побережья уже давно привлекают туристов со всего мира. Начиная с 21 века Анталия стремительно развивается и постепенно превращается в курорт мечты. Тысячи баров, ресторанов, отелей и иных развлекательных заведений, были построены благодаря большой туристической активности. Инфраструктура находится не в самом лучшем состоянии, однако, ее совершенствование, является одной из важнейших целей для турецких властей.'),
       (11, 'Куала-Лумпур', 'Сверкающие небоскребы, индуистские храмы и буддийские храмы стоят бок о бок в городской чаще. Торговые центры завалены печеньем, кренделями и хот-догами. Одна из главных достопримечательностей, памятник в честь погибших в борьбе Малайзии за свободу, с мускулистыми солдатами, оформленный в стиле “Голливудского блокбастера”.'),
       (12, 'Стамбул', 'Без тени сомнения, Стамбул – как Лондон, Нью-Йорк или Париж – один из величайших городов в мире. Расположенный на двух континентах Европы и Азии, разделенный проливом Босфор, нет ни одного другого города, который мог бы похвастаться тем же. '),
       (13, 'Нью-Йорк', 'Почему стоит посетить? Потому что это самый захватывающий город в мире, ни один другой не имеет такого огромного количества развлечений, ресторанов, баров, ночных клубов, а также, музеев, памятников и других значимых достопримечательностей. Местные жители также удивительно дружелюбны.'),
       (14, 'Шеньчжень', 'Имеет знаменитый тематический парк “Окно в Мир”, который состоит из 130 реконструированных моделей, самых известных достопримечательностей со всего мира. Располагаются они прямо на земле и некоторые из них имеют внушительные размеры. Другой достопримечательностью является “Минск Мир” – списанный бывший советский авианосец названый в честь столицы республики Беларусь.'),
       (15, 'Дубай', 'Город в пустыне? Это один из современнейших и передовых городов в мире. Здесь есть все для лучшего отдыха, теплое море, лучшие отели, грандиозные строения, сафари, дайвинг и многое другое. Вопреки слухам, Дубай также имеет богатую культуру и традиции, истоки которых заложены еще до основания ультрасовременного города. Еще 80 лет назад, там и вправду была только пустыня, с населением до 800 человек.'),
       (16, 'Макао', 'В 16 веке, это был один из первых портов для португальских купцов и миссионеров, который позволял им торговать с Китаем. Позже был колонизирован Португалией. В состав Китая вернулся в 1999 году, с этих пор Макао является игорной столицей региона. Также город имеет ВВП на душу населения в размере $ 91000, что делает Макао четвертым богатейшим городом в мире.'),
       (17, 'Париж', 'На сколько хорошо Вы знаете Париж? Да, это один из самых известных городов в мире, многие туристы уже успели оценить его красоты и достопримечательности. Но город не стоит на месте, он всегда меняется, старые районы проходят реконструкцию, появляются новые и новые места. Даже заядлые туристы, возвращаются сюда вновь, чтобы открыть для себя что-то новое.'),
       (18, 'Бангкок', 'Столица Таиланда, расположенный на юго-востоке, является самым загадочным и притягательным городом во всей Азии. Любой турист может опешить, увидев, такое столкновение между традицией и современностью. Большие небоскребы, дворцы и храмы, обилие запахов, вкусов и достопримечательностей, вряд ли возможно забыть.'),
       (19, 'Сингапур', 'К природе здесь относятся особенно чутко, что позволяет создавать природные шедевры. Одна из самых ярких достопримечательностей города, это футуристические сады. Которые можно описать так: Это ослепительный триумвират набережных садов, облаченных в био-купола из стекла и стали с поддержкой климат-контроля. Сингапур, преобладает повышенным количеством экзотики, при сопровождении ультро-современных построек.'),
       (20, 'Гонконг', 'Микс фэн-шуя и финансов, трамваи и храмы, цветочные сады, парки, буддистские монастыри, музеи, достопримечательности и Диснейленд – вот начальный список, того что привлекает туристов каждый год. Гонконг является самым популярным городом в мире, его посещают десятки миллионов людей. Это уникальное место, где люди ищут что-то свое и обязательно находят!'),
       (21, 'Лондон', 'В Лондоне, история сталкивается с искусством, модой, едой, и хорошим британским элем. Каждый турист сможет найти то, что его интересует. Любители культуры не должны пропустить “современную галерею Тейт” и конечно “Королевский оперный театр”. А если вы любите моду, то улица Оксфорд-стрит имеет около 500 торговых точек. Любителям кино и книг, будет интересно посетить музей Шерлока Холмса, который располагается, конечно же на ул. Бейкер-стрит 221B.');